// Accordion

$(document).ready(function() {
  $('.accordion-list-item:first-child').addClass('active').find('.accordion-content').css('display', 'block');
  $('.accordion-list-item').on('click', function(){
    if(!$(this).hasClass('active')){
      $(this).prevAll().removeClass('active').find('.accordion-content').slideUp();
     $(this).nextAll().removeClass('active').find('.accordion-content').slideUp();
    $(this).addClass('active');
    $(this).find(".accordion-content").slideDown();
} else{
   $(this).find(".accordion-content").slideUp();
 $(this).prevAll().removeClass('active').find('.accordion-content').slideUp();
     $(this).nextAll().removeClass('active').find('.accordion-content').slideUp();
   $(this).removeClass('active');
}
  });

});

